INSERT INTO `omo_question` VALUES (2342,153,0,'1929-TN-Kí hiệu, bảng chú giải, tìm đường-TH- 12','Khi biểu hiện các vùng trồng trọt và chăn nuôi thường dùng loại ký hiệu nào sau đây?',1,'Đối tượng thể hiện kí hiệu diện tích như: các khu vực trồng lúa, khu vực trồng cây lâu năm, khu vực dân cư, thành thị,...',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210907032329+PqwIDG','dongda.hocmai.com+210907032329+ygNsUG',0,1630985009,1630985009,11,11,NULL),(2343,153,0,'1929-TN-Kí hiệu, bảng chú giải, tìm đường-TH- 13','Cách đọc bản đồ đúng là',1,'Mục đích của việc đọc bản đồ là tìm ra đặc điểm của các đối tượng và mối quan hệ của chúng trên bản đồ. Đọc hiểu các đối tượng từ đó chỉ ra được mối liên hệ về giữa chúng. Ví dụ: Đọc một con sông ở bản đồ địa hình, ta phải thấy được mối quan hệ giữa hướng chảy, độ dốc lòng sông với địa hình ở khu vực đó,…',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210907032329+7ZYGdL','dongda.hocmai.com+210907032329+DRvv3N',0,1630985009,1630985009,11,11,NULL),(2344,153,0,'1929-TN-Kí hiệu, bảng chú giải, tìm đường-TH- 14','Để thể hiện một con vật (trâu, bò, hươu) trên bản đồ, người ta dùng kí hiệu nào sau đây?',1,'Để thể hiện một con vật (trâu, bò, hươu,…) trên bản đồ, người ta dùng kí hiệu tượng hình.',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210907032329+L3n1z8','dongda.hocmai.com+210907032329+UUfyCn',0,1630985009,1630985009,11,11,NULL),(2345,153,0,'1929-TN-Kí hiệu, bảng chú giải, tìm đường-VD- 15','Bản đồ địa hình thường được sử dụng trong ngành nào sau đây?',1,'Lĩnh vực quân sự sử dụng bản đồ nhằm phân tích các căn cứ chiến đấu, hướng tiến công thích hợp. Sử dụng bản đồ địa hình để phân tích được địa thế của khu vực tác chiến, các căn cứ quân sự (là nơi nhiều đồi núi hay đồng bằng, biết được đặc điểm địa hình sông ngòi) để từ đó tìm ra phương pháp chiến đấu phù hợp.',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210907032329+9avtqt','dongda.hocmai.com+210907032329+cYD5PW',0,1630985009,16309850