INSERT INTO `omo_question` VALUES (31394,2096,0,'1929-TN-Khí quyển, các khối khi, khí áp và gió-NB- 07','Trong các thành phần của không khí chiếm tỉ trọng lớn nhất là',1,'Thành phần của không khí là Nitơ (78%), Ôxi (21%), hơi nước và các khí khác (1%).',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210908030536+SjDhE8','dongda.hocmai.com+210908030536+Ko2Jl3',0,1631070336,1631070336,11,11,NULL),(31395,2096,0,'1929-TN-Khí quyển, các khối khi, khí áp và gió-NB- 10','Dựa vào tiêu chí nào sau đây để đặt tên cho các khối khí?',1,'Đặt tên khối khí dựa vào vị trí hình thành, tính chất và nhiệt độ (vùng có vĩ độ thấp; vùng có vĩ độ cao) và bề mặt tiếp xúc (trên biển và đại dương; trên đất liền).',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210908030536+25NbeR','dongda.hocmai.com+210908030536+eCyuzI',0,1631070336,1631070336,11,11,NULL),(31396,2096,0,'1929-TN-Khí quyển, các khối khi, khí áp và gió-NB- 12','Tầng nào sau đây của khí quyển nằm sát mặt đất?',1,'Thứ tự các tầng khí quyển theo thứ tự sát mặt đất lên cao là tầng đối lưu, tầng bình lưu và các tầng cao của khí quyển.',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210908030536+MgBnGU','dongda.hocmai.com+210908030536+B4bySE',0,1631070336,1631070336,11,11,NULL),(31397,2096,0,'1929-TN-Khí quyển, các khối khi, khí áp và gió-NB- 13','Đặc điểm của tầng đối lưu khác với các tầng khí quyển khác ở đặc điểm nào sau đây?',1,'Đặc điểm của tầng đối lưu khác với các tầng khí quyển khác ở điểm tầng đối lưu tập trung 90% không khí, hơi nước, bụi bặm,… và càng lên cao nhiệt độ không khí càng giảm (cứ lên cao 100m nhiệt độ giảm 0,6<sup>0</sup>C).',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210908030536+uiMPVz','dongda.hocmai.com+210908030536+TQhj70',0,1631070336,1631070336,11,11,NULL),(31398,2096,0,'1929-TN-Khí quyển, các khối khi, khí áp và gió-NB- 14','Các hiện tượng khí tượng tự nhiên như: mây, mưa, sấm, chớp... hầu hết xảy ra ở tầng nào sau đây?',1,'Đặc điểm tầng đối lưu là\n<div>- Giớ