INSERT INTO `omo_question` VALUES (48544,3070,0,'NHT11_Phep_doi_xung_truc_TN_TH_028','Cho đường thẳng $$ \\left( d \\right): -x+3y-1=0 $$ và $$ M\\left( 3;5 \\right) $$ . Tọa độ điểm $$ M\' $$ là điểm đối xứng với $$ M $$ qua $$ d $$ là',1,'Gọi \\[M\'(x\';y\')\\] là ảnh của \\[M\\]qua phép đối xứng trục<br /> \\[\\begin{array}{l}<br /> \\left\\{ \\begin{array}{l}<br /> x\'=x-\\frac{2. \\left( -1 \\right)\\left( -x+3y-1 \\right)}{{{1}^{2}}+{{3}^{2}}} \\\\<br /> y\'=y-\\frac{2. 3\\left( -x+3y-1 \\right)}{{{1}^{2}}+{{3}^{2}}} <br /> \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l}<br /> x\'=\\frac{10x-\\left( 2x-6y+2 \\right)}{10} \\\\<br /> y\'=\\frac{10y-\\left( -6x+18y-6 \\right)}{10} <br /> \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l}<br /> x\'=\\frac{8x+6y-2}{10} \\\\<br /> y\'=\\frac{6x-8y+6}{10} <br /> \\end{array} \\right. \\Rightarrow \\left\\{ \\begin{array}{l}<br /> x\'=\\frac{8. 3+6. 5-2}{10} \\\\<br /> y\'=\\frac{6. 3-8. 5+6}{10} <br /> \\end{array} \\right. \\\\<br /> \\Leftrightarrow \\left\\{ \\begin{array}{l}<br /> x\'=\\frac{26}{5} \\\\<br /> y\'=\\frac{-8}{5} <br /> \\end{array} \\right. <br /> \\end{array}\\]',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210908080222+4enYB4','dongda.hocmai.com+210908080222+KQMmAB',0,1631088142,1631088142,11,11,NULL),(48545,3070,0,'NHT11_Phep_doi_xung_truc_TN_TH_029','Cho hai đường thẳng \\[\\left( d \\right): \\frac{x-1}{1}=\\frac{y-3}{-2}\\] và \\[\\left( \\Delta<br /> \\right): \\frac{\\text{x}+1}{-3}=\\frac{\\text{y}+\\text{2}}{6}\\]. Đường thẳng \\[{{\\Delta }_{1}}\\]đối xứng \\[\\Delta \\] qua $$ d $$ có phương trình là:',1,'\\[\\left( d \\right): 2x+y-5=0\\] và \\[\\left( \\Delta<br /> \\right): 2x+y+4=0\\]<br /> Lấy điểm \\[M\\left( x;y \\right)\\in \\Delta \\]qua phép đối xứng trục ta được ảnh \\[M\'\\left( x\';y\' \\right)\\in \\Delta \'\\]<br /> Ta có. \\[\\left\\{ \\begin{array}{l}<br /> x\'=\\frac{-3x-4y+20}{5} \\\\<br /> y\'=\\frac{-4x+3y+10}{5} <br /> \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l}<br /> x=\\frac{-3x\'-4y\'+20}{5} \\\\<br /> y=\\frac{-4x\'+3y\'+10}{5} <br /> \\end{array} \\right. \\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\left( * \\right)\\]<br /> \\[M\\in \\Delta \\]nên thay \\[\\left( * \\right)\\] vào phương trình \\[\\left( \\Delta<br /> \\right): 2x\'+y\'-14=0\\]<br /> Phương trình đường thẳng \\[\\Delta \': 2x+y-14=0\\Rightarrow \\frac{x-\\frac{31}{5}}{1}=\\frac{y-\\frac{8}{5}}{-2}\\Leftrightarrow 2x+y-14=0\\]',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210908080222+RUJ8fR','dongda.hocmai.com+210908080222+jvIy4E',0,1631088142,1631088142,11,11,NULL),(48546,3070,0,'NHT11_Phep_doi_xung_truc_TN_TH_030','Đường thẳng đối xứng với đường thẳng $$ d: \\left\\{ \\begin{array}{l}<br /> x=1-t \\\\<br /> y=2+t <br /> \\end{array} \\right. $$ qua đường thẳng $$ \\Delta : \\frac{x}{2}=\\frac{y-3}{-1} $$ có phương trình là:',1,'\\[\\begin{array}{l}<br /> \\left( \\Delta<br /> \\right): x+2y-6=0 \\\\<br /> \\left( d \\right): x+y-3=0 <br /> \\end{array}\\]<br /> Lấy điểm \\[M\\left( x;y \\right)\\in d\\]qua phép đối xứng trục ta được ảnh \\[M\'\\left( x\';y\' \\right)\\in d\'\\]<br /> Ta có. \\[\\left\\{ \\begin{array}{l}<br /> x\'=\\frac{3x-4y+12}{5} \\\\<br /> y\'=\\frac{-4x-3y+24}{5} <br /> \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l}<br /> x=\\frac{3x\'-4y\'+12}{5} \\\\<br /> y=\\frac{-4x\'-3y\'+24}{5} <br /> \\end{array} \\right. \\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\left( * \\right)\\]<br /> \\[M\\in \\Delta \\]nên thay \\[\\left( * \\right)\\] vào phương trình \\[\\left( d \\right)\\]là \\[\\,\\,\\,x\'+7y\'-21=0\\]<br /> Phương trình đường thẳng \\[d\': x+7y-21=0\\Rightarrow \\left\\{ \\begin{array}{l}<br /> x=7t \\\\<br /> y=3-t <br /> \\end{array} \\right. \\]',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210908080222+bxiPTZ','dongda.hocmai.com+210908080222+j3QiBn',0,1631088142,1631088142,11,11,NULL),(48547,3070,0,'NHT11_Phe