INSERT INTO `omo_question_answers` VALUES (79228,26716,'vừa bị oxi hóa, vừa bị khử.',1,0.0000000,'Sai, vì số oxi hóa của Cu chỉ bị giảm từ +2 xuống 0 nên ion Cu<sup>2+ </sup>chỉ bị khử.',1),(79229,26717,'4Na + O<sub>2</sub> \\[\\to\\] 2Na<sub>2</sub>O',1,1.0000000,'Đúng, vì trong phản ứng này số oxi hóa của Na tăng từ 0 đến +1 và số oxi hóa của O giảm từ 0 xuống -2.',1),(79230,26717,'2Fe(OH)<sub>3</sub> \\[\\to\\] Fe<sub>2</sub>O<sub>3</sub> + 3H<sub>2</sub>O',1,0.0000000,'Sai, vì trong phản ứng này không có sự thay đổi số oxi hóa của các nguyên tố. Trước và sau phản ứng thì số oxi hóa của H vẫn là +1, số oxi hóa của Fe vẫn là +3 và số oxi hóa của O vẫn là -2.',1),(79231,26717,'Na<sub>2</sub>CO<sub>3</sub> + H<sub>2</sub>SO<sub>4</sub> \\[\\to\\] Na<sub>2</sub>SO<sub>4</sub> + H<sub>2</sub>O + CO<sub>2</sub>\\[\\uparrow\\]',1,0.0000000,'Sai, vì trong phản ứng này không có sự thay đổi số oxi hóa của các nguyên tố. Trước và sau phản ứng thì số oxi hóa của H vẫn là +1, số oxi hóa của Na vẫn là +1, số oxi hóa của O vẫn là -2 và số oxi hóa của S vẫn là +6.',1),(79232,26717,'NH<sub>3</sub> + HCl \\[\\to\\] NH<sub>4</sub>Cl',1,0.0000000,'Sai, vì trong phản ứng này không có sự thay đổi số oxi hóa của các nguyên tố. Trước và sau phản ứng thì số oxi hóa của H vẫn là +1, số oxi hóa của N vẫn là -3 và số oxi hóa của Cl vẫn là -1.',1),(79233,26718,'nhận một electron.',1,1.0000000,'Đúng, vì số oxi hóa của brôm giảm từ 0 xuống -1.',1),(79234,26718,'nhường một electron.',1,0.0000000,'Sai, vì số oxi hóa của brôm giảm từ 0 xuống -1 nên nguyên tử brôm phải nhận electron để trở thành ion brômua.',1),(79235,26718,'nhận một proton.',1,0.0000000,'Sai, vì nguyên tử brôm phải nhận một electron.',1),(79236,26718,'nhường một proton.',1,0.0000000,'Sai, vì nguyên tử brôm phải nhận một electron.',1),(79237,26719,'chỉ bị oxi hóa.',1,0.0000000,'Sai, vì số oxi hóa của Cl còn giảm từ 0 xuống -1 nên Cl còn bị khử.',1),(79238,26719,'chỉ bị khử.',1,0.0000000,'Sai, vì số oxi hóa của Cl còn tăng từ 0 đến