INSERT INTO `omo_question_attempts` VALUES (24,5,1,'manualgraded',22,1,1.0000000,0.0000000,1.0000000,0,'Dạng 1. Biểu diễn tập hợp, phần tử thuộc tập hợp.\nCho tập hợp \\[A\\] gồm các số tự nhiên lớn hơn \\[7\\] và nhỏ hơn \\[12\\]. Hãy liệt kê các phần tử của tập hợp \\[A\\] sau đó điền kí hiệu thích hợp vào các chỗ chấm sau:\n$$ 9\\,...\\,A $$ ;\n$$ 14\\,...\\,A $$ ;\n$$ 7\\,...\\,A $$ ;\n$$ 12\\,...\\,A $$ .',NULL,NULL,1626946604),(25,5,2,'manualgraded',23,1,1.0000000,0.0000000,1.0000000,0,'Dạng 1. Biểu diễn tập hợp, phần tử thuộc tập hợp.\nLiệt kê các phần tử của mỗi tập hợp sau:\na) \\[A\\] là tập hợp các chữ cái xuất hiện trong từ “giáo dục”.\nb) \\[B\\] là tập hợp tên các mùa trong năm.\nc) \\[C\\] là tập hợp các chữ cái trong cụm từ “HỌC SINH”.',NULL,NULL,1627038244),(26,5,3,'manualgraded',24,1,1.0000000,0.0000000,1.0000000,0,'Dạng 1. Biểu diễn tập hợp, phần tử thuộc tập hợp.\nCho các tập hợp \\[A=\\left\\{ 1;2;\\,3 \\right\\}\\]; \\[B=\\left\\{ 7;\\,8 \\right\\}\\]. Viết các tập hợp trong đó mỗi tập hợp gồm:\na) Một phần tử thuộc \\[A\\] và một phần tử thuộc \\[B\\].\nb) Một phần tử thuộc \\[A\\] và hai phần tử thuộc \\[B\\].',NULL,NULL,1627038250),(27,5,4,'manualgraded',25,1,1.0000000,0.0000000,1.0000000,0,'Dạng 1. Biểu diễn tập hợp, phần tử thuộc tập hợp.\nCho tập hợp \\[A=\\left\\{ 10;\\,11;\\,12;\\,13;14;\\,15 \\right\\}\\]; \\[B=\\left\\{ 10;\\,15;\\,20 \\right\\}\\].\na) Viết tập hợp \\[C\\] gồm các phần tử thuộc \\[A\\] nhưng không thuộc \\[B\\].\nb) Viết tập hợp \\[D\\] gồm các phần tử thuộc \\[B\\] nhưng không thuộc \\[A\\].\nc) Viết tập hợp \\[E\\] gồm các phần tử vừa thuộc \\[A\\] vừa thuộc \\[B\\].',NULL,NULL,1627038257),(28,5,5,'manualgraded',26,1,1.0000000,0.0000000,1.0000000,0,'Dạng 1. Biểu diễn tập hợp, phần tử thuộc tập hợp.\nHãy viết tập hợp \\[A=\\left\\{ 1;\\,3;\\,5;\\,7;\\,9 \\right\\}\\] bằng cách chỉ ra tính chất đặc trưng của các phần tử thuộc tập hợp.',NULL,NULL,1626947079),(29,5,6,'manualgraded',28,1,5.0000000,0.0000000,1.0000000,0,'Dạng 1. Biểu diễn tập hợp, phần tử thuộc tập hợp.\nHãy viết tập hợp \\[C=\\left\\{ 1;\\,4;\\,9;\\,16;\\,25;\\,36;\\,49;\\,64;\\,81 \\right\\}\\] bằng cách chỉ ra tính chất đặc trưng của các phần tử thuộc tập hợp.',NULL,NULL,1626947082),(30,5,7,'manualgraded',27,1,5.0000000,0.0000000,1.0000000,0,'Dạng 1. Biểu diễn tập hợp, phần tử thuộc tập hợp.\nHãy viết tập hợp \\[B=\\left\\{ 0;\\,5;\\,10;\\,15;\\ldots ;\\,100 \\right\\}\\] bằng cách chỉ ra tính chất đặc trưng của các phần tử thuộc tập hợp.',NULL,NULL,1626947085),(31,5,8,'manualgraded',29,1,4.0000000,0.0000000,1.0000000,0,'Dạng 2. Các bài toán thực tiễn.\nHiện nay theo xu hướng ở các nước trên thế giới, rác thải được người dân phân loại và bỏ vào thùng rác gồm thùng đựng rác tái chế, thùng đựng rác không tái chế và thùng đựng chất thải nguy hại.\n[01]\nHãy viết dưới dạng liệt kê tập hợp \\[M\\] gồm các loại rác tái chế và tập hợp \\[N\\] gồm các loại rác không tái chế theo hình minh hoạ trên. ',NULL,NULL,1626947400),(32,5,9,'manualgraded',30,1,4.0000000,0.0000000,1.0000000,0,'Dạng 2. Các bài toán thực tiễn. a) Viết tập hợp các tháng của các quý trong năm. b) Viết tập hợp các tháng dương lịch có \\[30\\] ngày, có \\[31\\] ngày.',NULL,NULL,1626947419),(33,5,10,'deferredfeedback',31,1,1.0000000,0.0000000,1.0000000,0,'Viết tập hợp \\[A\\] gồm các phần tử \\[6,\\,7,\\,8,\\,9,\\,10\\] dưới dạng tập hợp, ta được: \\[A=\\left( 6;7;8;9;10 \\right)\\].; $$ A=\\left\\{ 6,\\,7,\\,8,\\,9,\\,10 \\right\\} $$ .; $$ A=\\left\\{ 6;\\,7;\\,8;\\,9;\\,10 \\right\\} $$ .; $$ A=6,\\,7,\\,8,\\,9,\\,10 $$ .','$$ A=\\left\\{ 6;\\,7;\\,8;\\,9;\\,10 \\right\\} $$ .',NULL,1626947494),(34,5,11,'deferredfeedback',32,1,1.0000000,0.0000000,1.0000000,0,'Cho tập hợp \\[M=\\left\\{ 2;\\,4;\\,6 \\right\\}\\]. Khẳng định nào sau đây là đúng: Số \\[6\\] không là phần tử của tập hợp \\[M\\].; Số \\[3\\] là phần tử của tập hợp \\[M\\].; Số \\[2\\] không là phần tử của tập hợp \\[M\\].; Số \\[4\\] là phần tử của tập hợp \\[M\\].','Số \\[4\\] là phần tử của tập hợp \\[M\\].',NULL,1626947504),(35,5,12,'deferredfeedback',33,1,1.0000000,0.0000000,1.0000000,0,'Viết tập hợp \\[A\\] các số tự nhiên lớn hơn \\[5\\] và nhỏ hơn \\[10\\]: $$ A=\\left\\{ 5;\\,6;\\,7;\\,8;\\,9 \\right\\} $$ .; $$ A=\\left\\{ 6;\\,7;\\,8;\\,9;\\,10 \\right\\} $$ .; $$ A=\\left\\{ 6;\\,7;\\,8 \\right\\} $$ .; $$ A=\\left\\{ 6;\\,7;\\,8;\\,9 \\right\\} $$ .','$$ A=\\left\\{ 6;\\,7;\\,8;\\,9 \\right\\} $$ .',NULL,1627038228),(36,5,13,'deferredfeedback',34,1,1.0000000,0.0000000,1.0000000,0,'Cho tập hợp $$ A=\\left\\{ 1;2;3;4;5;6 \\right\\} $$ , $$ B=\\left\\{ 2;4;6;8 \\right\\} $$ . Chọn phương án đúng: \\[8\\in A\\].; $$ 0\\in A,3\\in B $$ .; \\[4\\in A;4\\notin B\\].; \\[5\\notin B\\].','\\[5\\notin B\\].',NULL,1626947107),(37,5,14,'deferredfeedback',35,1,1.0000000,0.0000000,1.0000000,0,'Cho tập hợp $$ A=\\left\\{ 1;2;3;4 \\right\\} $$ và tập hợp $$ B=\\left\\{ 3;4;5 \\right\\} $$ . Tập hợp \\[C\\] gồm các phần tử thuộc tập hợp \\[A\\] nhưng không thuộc tập
