INSERT INTO `omo_question` VALUES (11596,768,0,'NHVL8_TN_Tinh cong co hoc_TH_015','Mt my bay trc thng khi ct cnh, ng c to ra mt lc pht ng l F =10500N, sau thi gian t = 90 giy my bay t c  cao l h = 850 m. Tnh cng ca ng c my bay trong thi gian ct cnh.',1,'Cng ca ng c my bay trong thi gian ct cnh:<br /> A =F. s = F. h = 10500. 850 = 8925000 (J) = 8925 (kJ)<br />',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210907140119+2VAyyb','dongda.hocmai.com+210907140119+jXys1F',0,1631023279,1631023279,3,3,NULL),(11597,768,0,'NHVL8_TN_Tinh cong co hoc_VD_009','Mt u tu ko mt on tu chuyn ng t ga C n ga D trong 15 pht vi vn tc 30km/h. Ti ga D on tu c mc thm toa v chuyn ng u t ga D n ga E vi vn tc nh hn trc 10km/h. Thi gian i t ga D n ga E l 30 pht. Tnh cng ca u tu  sinh ra bit rng lc ko ca u tu khng i l 40 000N?',1,'Qung ng tu i c t ga C n D l: s<sub>1</sub> = v<sub>1</sub>. t<sub>1</sub> = 30. (15: 60) = 7,5 km/h<br /> Qung ng tu i c t ga D n E l: s<sub>2</sub> = (30  10). (30: 60) = 10 km/h<br /> Tng qung ng tu i c: s = s<sub>1</sub> + s<sub>2</sub> = 17,5 km = 17 500 m<br /> Cng ca tu  sinh ra: A = F. s = 40 000. 17 500 = 700 000 000J',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210907140119+5BBKqw','dongda.hocmai.com+210907140119+BYCEuF',0,1631023279,1631023279,3,3,NULL),(11598,768,0,'NHVL8_TN_Tinh cong co hoc_VD_010','Ngi ta dng mt cn cu  nng mt thng hng khi lng 2500kg ln  cao 12m. Tnh cng thc hin c trong trng hp ny',1,'Trng lng ca thng hng: P = 10. m = 10. 2500 = 25000 (N)<br /> Cng thc hin c trong trng hp ny l: A = F. s = P. h = 25000. 12 = 300000 (J) = 300 (kJ)',1,1.0000000,0.0000000,'multichoice',1,'dongda.hocmai.com+210907140119+5W30bn','dongda.hocmai.com+210907140119+NSLb8k',0,1631023279,1631023279,3,3,NULL),(11599,768,0,'NHVL8_TN_Tinh cong co hoc_VD_012','Mt nhm hc sinh y mt xe ch t i t A n B trn mt on ng bng phng nm ngang. n B  ht t trn xe xung ri li y xe i theo ng c v A. So snh cng sinh ra  lt i v lt v. Cu tr li no di y ng?',1,'Ta bit A= F. s<br