INSERT INTO `omo_question_attempts` VALUES (24,5,1,'manualgraded',22,1,1.0000000,0.0000000,1.0000000,0,'Dng 1. Biu din tp hp, phn t thuc tp hp.\nCho tp hp \\[A\\] gm cc s t nhin ln hn \\[7\\] v nh hn \\[12\\]. Hy lit k cc phn t ca tp hp \\[A\\] sau  in k hiu thch hp vo cc ch chm sau:\n$$ 9\\,...\\,A $$ ;\n$$ 14\\,...\\,A $$ ;\n$$ 7\\,...\\,A $$ ;\n$$ 12\\,...\\,A $$ .',NULL,NULL,1626946604),(25,5,2,'manualgraded',23,1,1.0000000,0.0000000,1.0000000,0,'Dng 1. Biu din tp hp, phn t thuc tp hp.\nLit k cc phn t ca mi tp hp sau:\na) \\[A\\] l tp hp cc ch ci xut hin trong t gio dc.\nb) \\[B\\] l tp hp tn cc ma trong nm.\nc) \\[C\\] l tp hp cc ch ci trong cm t HC SINH.',NULL,NULL,1627038244),(26,5,3,'manualgraded',24,1,1.0000000,0.0000000,1.0000000,0,'Dng 1. Biu din tp hp, phn t thuc tp hp.\nCho cc tp hp \\[A=\\left\\{ 1;2;\\,3 \\right\\}\\]; \\[B=\\left\\{ 7;\\,8 \\right\\}\\]. Vit cc tp hp trong  mi tp hp gm:\na) Mt phn t thuc \\[A\\] v mt phn t thuc \\[B\\].\nb) Mt phn t thuc \\[A\\] v hai phn t thuc \\[B\\].',NULL,NULL,1627038250),(27,5,4,'manualgraded',25,1,1.0000000,0.0000000,1.0000000,0,'Dng 1. Biu din tp hp, phn t thuc tp hp.\nCho tp hp \\[A=\\left\\{ 10;\\,11;\\,12;\\,13;14;\\,15 \\right\\}\\]; \\[B=\\left\\{ 10;\\,15;\\,20 \\right\\}\\].\na) Vit tp hp \\[C\\] gm cc phn t thuc \\[A\\] nhng khng thuc \\[B\\].\nb) Vit tp hp \\[D\\] gm cc phn t thuc \\[B\\] nhng khng thuc \\[A\\].\nc) Vit tp hp \\[E\\] gm cc phn t va thuc \\[A\\] va thuc \\[B\\].',NULL,NULL,1627038257),(28,5,5,'manualgraded',26,1,1.0000000,0.0000000,1.0000000,0,'Dng 1. Biu din tp hp, phn t thuc tp hp.\nHy vit tp hp \\[A=\\left\\{ 1;\\,3;\\,5;\\,7;\\,9 \\right\\}\\] bng cch ch ra tnh cht c trng ca cc phn t thuc tp hp.',NULL,NULL,1626947079),(29,5,6,'manualgraded',28,1,5.0000000,0.0000000,1.0000000,0,'Dng 1. Biu din tp hp, phn t thuc tp hp.\nHy vit tp hp \\[C=\\left\\{ 1;\\,4;\\,9;\\,16;\\,25;\\,36;\\,49;\\,64;\\,81 \\right\\}\\] bng cch ch ra tnh cht c trng ca cc phn t thuc tp hp.',NULL,NULL,1626947082)